const led = class {
    constructor(pin){
        this.pin = pin
        this.Gpio = require('onoff').Gpio
    }
    init(){
        this.LED = new this.Gpio(this.pin, 'out')
    }

    init(state){
        this.LED = new this.Gpio(this.pin , 'out')
        this.LED.writeSync(state)
    }

    on(){
        this.LED.writeSync(1)
    }
    off(){
        this.LED.writeSync(0)
    }
}

module.exports = {
    led: led
}